@layer utilities {
  /* Sharp Shot Design System Utilities */
  .tungsten-style {
    font-family: 'Saira Condensed', sans-serif;
    font-weight: 700;
    font-style: italic;
    transform: skew(-5deg);
    line-height: 0.9;
  }

  .surface-glass {
    background: hsl(var(--surface-glass));
    backdrop-filter: blur(12px);
    border: 1px solid hsl(var(--surface-glass-border));
  }

  .surface-glass-hover {
    transition: all 0.3s ease;
  }

  .surface-glass-hover:hover {
    transform: translateY(-2px);
    box-shadow: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 10px 10px -5px rgb(0 0 0 / 0.04);
    border-color: hsl(var(--border));
  }

  .btn-navy {
    background: hsl(var(--sharp-navy));
    color: hsl(var(--primary-foreground));
    border: 2px solid transparent;
    transition: all 0.3s ease;
  }

  .btn-navy:hover {
    background: hsl(var(--sharp-navy) / 0.9);
    border-color: hsl(var(--sharp-gold));
  }

  .btn-navy:focus {
    outline: none;
    ring: 2px solid hsl(var(--sharp-gold) / 0.2);
  }

  .text-hero {
    font-size: clamp(3rem, 8vw, var(--text-hero));
  }

  .text-heading {
    font-size: clamp(2rem, 5vw, var(--text-heading));
  }

  .text-subheading {
    font-size: clamp(1.25rem, 3vw, var(--text-subheading));
  }

  .tag-chip {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.375rem 0.75rem;
    border-radius: 9999px;
    border: 1px solid hsl(var(--border) / 0.5);
    background: hsl(var(--muted) / 0.3);
    font-size: var(--text-xs);
    color: hsl(var(--muted-foreground));
  }

  .tag-chip-dot {
    width: 0.375rem;
    height: 0.375rem;
    border-radius: 50%;
    background: hsl(var(--sharp-gold));
  }

  /* Hero Glimmer Animation */
  @keyframes goldGlimmer {
    0% {
      transform: translateX(-100%);
      opacity: 0;
    }
    50% {
      opacity: 1;
    }
    100% {
      transform: translateX(100%);
      opacity: 0;
    }
  }

  .hero-glimmer {
    position: relative;
    overflow: hidden;
  }

  .hero-glimmer::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      90deg,
      transparent,
      rgba(216, 172, 53, 0.3),
      rgba(216, 172, 53, 0.6),
      rgba(216, 172, 53, 0.3),
      transparent
    );
    animation: goldGlimmer 2s ease-out 1s 1;
    pointer-events: none;
  }

  /* 3D Spinning Sharp Shot Logo with Signal Rings */
  .logo-container {
    position: relative;
    width: 300px;
    height: 300px;
    margin: 0 auto;
    perspective: 1000px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .spinning-3d-logo {
    width: 200px;
    height: 200px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    z-index: 2;
  }

  .logo-3d {
    width: 200px;
    height: 200px;
    object-fit: contain;
    animation: logo3DRotate 8s linear infinite;
    filter: 
      drop-shadow(0 10px 30px rgba(216, 172, 53, 0.4))
      drop-shadow(3px 3px 0px rgba(180, 140, 45, 0.8))
      drop-shadow(6px 6px 0px rgba(150, 115, 38, 0.6))
      drop-shadow(9px 9px 0px rgba(120, 90, 30, 0.4));
    transform-style: preserve-3d;
    position: relative;
  }

  .logo-3d::before {
    content: '';
    position: absolute;
    top: 3px;
    left: 3px;
    width: 100%;
    height: 100%;
    background-image: url('/attached_assets/Gold_StarLeaf_1755739948433.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    filter: brightness(0.6) saturate(0.7) hue-rotate(15deg);
    z-index: -1;
    pointer-events: none;
  }

  .logo-3d::after {
    content: '';
    position: absolute;
    top: 6px;
    left: 6px;
    width: 100%;
    height: 100%;
    background-image: url('/attached_assets/Gold_StarLeaf_1755739948433.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    filter: brightness(0.4) saturate(0.5) hue-rotate(30deg);
    z-index: -2;
    pointer-events: none;
  }

  /* Animated Signal Rings */
  .pulse-ring {
    position: absolute;
    border: 2px solid rgba(216, 172, 53, 0.4);
    border-radius: 50%;
    animation: goldSignalPulse 4s ease-out infinite;
  }

  .pulse-ring:nth-child(1) {
    width: 300px;
    height: 300px;
    top: 25px;
    left: 0px;
    animation-delay: 0s;
  }

  .pulse-ring:nth-child(2) {
    width: 340px;
    height: 340px;
    top: 5px;
    left: -20px;
    animation-delay: 1.3s;
  }

  .pulse-ring:nth-child(3) {
    width: 380px;
    height: 380px;
    top: -15px;
    left: -40px;
    animation-delay: 2.6s;
  }

  .pulse-ring:nth-child(1) {
    width: 320px;
    height: 320px;
    top: -10px;
    left: -10px;
    animation-delay: 0s;
  }

  .pulse-ring:nth-child(2) {
    width: 360px;
    height: 360px;
    top: -30px;
    left: -30px;
    animation-delay: 1s;
  }

  .pulse-ring:nth-child(3) {
    width: 400px;
    height: 400px;
    top: -50px;
    left: -50px;
    animation-delay: 2s;
  }

  @keyframes logo3DRotate {
    0% { 
      transform: rotateY(0deg) rotateX(5deg) rotateZ(0deg);
    }
    12.5% {
      transform: rotateY(45deg) rotateX(8deg) rotateZ(2deg);
    }
    25% { 
      transform: rotateY(90deg) rotateX(10deg) rotateZ(5deg);
    }
    37.5% {
      transform: rotateY(135deg) rotateX(8deg) rotateZ(2deg);
    }
    50% { 
      transform: rotateY(180deg) rotateX(5deg) rotateZ(0deg);
    }
    62.5% {
      transform: rotateY(225deg) rotateX(-8deg) rotateZ(-2deg);
    }
    75% { 
      transform: rotateY(270deg) rotateX(-10deg) rotateZ(-5deg);
    }
    87.5% {
      transform: rotateY(315deg) rotateX(-8deg) rotateZ(-2deg);
    }
    100% { 
      transform: rotateY(360deg) rotateX(5deg) rotateZ(0deg);
    }
  }

  @keyframes goldSignalPulse {
    0% {
      opacity: 0.8;
      transform: scale(0.85);
      border-color: rgba(216, 172, 53, 0.6);
    }
    25% {
      opacity: 0.6;
      border-color: rgba(216, 172, 53, 0.4);
    }
    50% {
      opacity: 0.4;
      border-color: rgba(216, 172, 53, 0.2);
    }
    75% {
      opacity: 0.2;
      border-color: rgba(216, 172, 53, 0.1);
    }
    100% {
      opacity: 0;
      transform: scale(1.2);
      border-color: rgba(216, 172, 53, 0);
    }
  }

  @media (max-width: 768px) {
    .logo-container {
      width: 200px;
      height: 200px;
    }

    .spinning-3d-logo {
      width: 120px;
      height: 120px;
    }

    .logo-3d {
      width: 120px;
      height: 120px;
    }

    .pulse-ring:nth-child(1) {
      width: 200px;
      height: 200px;
      top: 0px;
      left: 0px;
    }

    .pulse-ring:nth-child(2) {
      width: 220px;
      height: 220px;
      top: -10px;
      left: -10px;
    }

    .pulse-ring:nth-child(3) {
      width: 240px;
      height: 240px;
      top: -20px;
      left: -20px;
    }

    .pulse-ring:nth-child(1) {
      width: 220px;
      height: 220px;
      top: -10px;
      left: -10px;
    }

    .pulse-ring:nth-child(2) {
      width: 260px;
      height: 260px;
      top: -30px;
      left: -30px;
    }

    .pulse-ring:nth-child(3) {
      width: 300px;
      height: 300px;
      top: -50px;
      left: -50px;
    }
  }
}

