  --popover: hsl(0, 0%, 100%);
  --foreground: hsl(0, 0%, 0%);
  --card-foreground: hsl(0, 0%, 0%);
  --popover-foreground: hsl(0, 0%, 0%);

  /* 30% - Secondary color: Charcoal for headers, cards, text */
  --secondary: hsl(0, 0%, 23%);
  --secondary-foreground: hsl(0, 0%, 100%);
  --muted: hsl(0, 0%, 96%);
  --muted-foreground: hsl(0, 0%, 23%);
  --border: hsl(0, 0%, 89%);
  --input: hsl(0, 0%, 89%);

  /* 10% - Accent color: Gold for buttons, links, CTAs */
  --primary: hsl(45, 73%, 53%);
  --primary-foreground: hsl(0, 0%, 23%);
  --accent: hsl(45, 73%, 53%);
  --accent-foreground: hsl(0, 0%, 23%);

  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(45, 73%, 53%);
  --radius: 0.5rem;

