  transform: translateY(-1px);
}

/* Gold gradient buttons */
.gold-gradient {
  background: linear-gradient(90deg, #D8AC35, #E5C347);
  color: black;
}

/* Scroll fade-in animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-fadeInUp {
  animation: fadeInUp 0.5s ease-out forwards;
}

/* Smooth scrolling for glossary navigation */
html {
  scroll-behavior: smooth;
}

.fade-in-up {
  animation: fadeInUp 0.6s ease-out forwards;
}

.fade-in-delay-1 {
  animation-delay: 0.2s;
  opacity: 0;
}

.fade-in-delay-2 {
  animation-delay: 0.4s;
  opacity: 0;
}

.fade-in-delay-3 {
  animation-delay: 0.6s;
  opacity: 0;
}

/* Smooth scrolling for anchor links */
html {
  scroll-behavior: smooth;
}

.nav-link {
  position: relative;
  transition: color 0.2s ease;
}

.nav-link.active {
  color: var(--gold);
  font-weight: 600;
}

.nav-link.active::after {
  content: '';
  position: absolute;
  bottom: -8px;
  left: 0;
  right: 0;
  height: 2px;
  background: var(--gold);
}

/* Gold color utility */
.text-gold {
  color: hsl(45, 73%, 53%);
}

.bg-gold {
  background-color: hsl(45, 73%, 53%);
}

.border-gold {
  border-color: hsl(45, 73%, 53%);
}

.hover\:bg-gold:hover {
  background-color: hsl(45, 73%, 53%);
}

.hover\:text-gold:hover {
  color: hsl(45, 73%, 53%);
}

/* Charcoal color utility */
.text-charcoal {
  color: hsl(0, 0%, 23%);
}

.bg-charcoal {
  background-color: hsl(0, 0%, 23%);
}

.border-charcoal {
  border-color: hsl(0, 0%, 23%);
}

.hover\:bg-charcoal:hover {
  background-color: hsl(0, 0%, 23%);
}

/* Sharp Shot Gold Branding Consistency Toolkit */
/* Additional gold variants for comprehensive branding */
.text-gold-light {
  color: hsl(45, 73%, 65%);
}

.text-gold-dark {
  color: hsl(45, 73%, 40%);
}

.bg-gold-light {
  background-color: hsl(45, 73%, 65%);
}

.bg-gold-dark {
  background-color: hsl(45, 73%, 40%);
}



.border-gold-light {
  border-color: hsl(45, 73%, 65%);
}

.border-gold-dark {
  border-color: hsl(45, 73%, 40%);
}

/* Gold focus states */
.focus\:ring-gold:focus {
  --tw-ring-color: hsl(45, 73%, 53%);
}

.focus\:border-gold:focus {
  border-color: hsl(45, 73%, 53%);
}

/* Gold shadow utilities */
.shadow-gold {
  box-shadow: 0 1px 3px 0 hsla(45, 73%, 53%, 0.1), 0 1px 2px 0 hsla(45, 73%, 53%, 0.06);
}

.shadow-gold-lg {
  box-shadow: 0 10px 15px -3px hsla(45, 73%, 53%, 0.1), 0 4px 6px -2px hsla(45, 73%, 53%, 0.05);
}

/* Consistent button styles */
.btn-gold {
  background-color: hsl(45, 73%, 53%);
  color: hsl(0, 0%, 8%);
  border: 1px solid hsl(45, 73%, 53%);
  font-weight: 600;
  transition: all 0.2s ease-in-out;
}

.btn-gold:hover {
  background-color: hsl(45, 73%, 60%);
  border-color: hsl(45, 73%, 60%);
  transform: translateY(-1px);
}

.btn-gold:active {
  transform: translateY(0);
}

.btn-gold-outline {
  background-color: transparent;
  color: hsl(45, 73%, 53%);
  border: 2px solid hsl(45, 73%, 53%);
  font-weight: 600;
  transition: all 0.2s ease-in-out;
}

.btn-gold-outline:hover {
  background-color: hsl(45, 73%, 53%);
  color: hsl(0, 0%, 8%);
}

/* Dark mode adjustments for gold buttons */
.dark .btn-gold {
  color: hsl(0, 0%, 8%);
}

.dark .btn-gold-outline {
  color: hsl(45, 73%, 53%);
}

.dark .btn-gold-outline:hover {
  color: hsl(0, 0%, 8%);
}

