// Content Generator - Creates human-readable sports content

import { DataService } from '../services/DataService';
import { Game, Player, Team, LeagueStats, BettingData, NicheData } from '../types/ContentTypes';
import { Logger } from '../utils/Logger';

export class ContentGenerator {
  private dataService: DataService;
  private logger: Logger;

  constructor(dataService: DataService) {
    this.dataService = dataService;
    this.logger = new Logger();
  }

  /**
   * Generate game preview content
   */
  async generateGamePreview(game: Game): Promise<string> {
    const date = new Date(game.date).toLocaleDateString();
    const time = game.time || 'TBD';

    let content = `# Game Preview: ${game.team1City} ${game.team1Name} vs ${game.team2City} ${game.team2Name}\n\n`;
    content += `**Date:** ${date}\n`;
    content += `**Time:** ${time}\n`;
    content += `**League:** ${game.league}\n`;
    content += `**Sport:** ${game.sport}\n\n`;

    if (game.venue) {
      content += `**Venue:** ${game.venue}\n\n`;
    }

    if (game.odds) {
      content += `## Betting Lines\n\n`;
      content += `- **Spread:** ${game.team1City} ${game.odds.spread > 0 ? '+' : ''}${game.odds.spread}\n`;
      content += `- **Over/Under:** ${game.odds.overUnder}\n`;
      content += `- **Moneyline:** ${game.team1City} ${game.odds.moneyline1} | ${game.team2City} ${game.odds.moneyline2}\n\n`;
    }

    content += `## Matchup Analysis\n\n`;
    content += `This ${game.sport} matchup features ${game.team1City} ${game.team1Name} taking on ${game.team2City} ${game.team2Name}. `;
    content += `Both teams will be looking to secure a crucial victory in this ${game.league} contest.\n\n`;

    if (game.weather) {
      content += `**Weather:** ${game.weather}\n\n`;
    }

    content += `## Key Factors to Watch\n\n`;
    content += `- Home field advantage for the hosting team\n`;
    content += `- Recent form and momentum heading into the game\n`;
    content += `- Key player matchups and injury reports\n`;
    content += `- Historical head-to-head performance\n\n`;

    content += `*Preview generated on ${new Date().toLocaleString()}*\n`;

    return content;
  }

  /**
   * Generate game recap content
   */
  async generateGameRecap(game: Game): Promise<string> {
    const date = new Date(game.date).toLocaleDateString();
    const finalScore = `${game.team1Score} - ${game.team2Score}`;
    const winner = (game.team1Score || 0) > (game.team2Score || 0) 
      ? `${game.team1City} ${game.team1Name}` 
      : `${game.team2City} ${game.team2Name}`;

    let content = `# Game Recap: ${game.team1City} ${game.team1Name} vs ${game.team2City} ${game.team2Name}\n\n`;
    content += `**Final Score:** ${finalScore}\n`;
    content += `**Winner:** ${winner}\n`;
    content += `**Date:** ${date}\n`;
    content += `**League:** ${game.league}\n\n`;

    content += `## Game Summary\n\n`;
    content += `${winner} secured a victory with a final score of ${finalScore} in this ${game.league} matchup. `;

    const marginOfVictory = Math.abs((game.team1Score || 0) - (game.team2Score || 0));
    if (marginOfVictory <= 3) {
      content += `It was a closely contested game decided by just ${marginOfVictory} point${marginOfVictory !== 1 ? 's' : ''}.\n\n`;
    } else if (marginOfVictory >= 20) {
      content += `The game turned into a dominant performance with a ${marginOfVictory}-point margin of victory.\n\n`;
    } else {
      content += `The winning team controlled the game with a solid ${marginOfVictory}-point victory.\n\n`;
    }

    if (game.highlights && game.highlights.length > 0) {
      content += `## Game Highlights\n\n`;
      game.highlights.forEach((highlight, index) => {
        content += `${index + 1}. ${highlight}\n`;
      });
      content += `\n`;
    }

    if (game.keyStats) {
      content += `## Key Statistics\n\n`;
      Object.entries(game.keyStats).forEach(([stat, value]) => {
        content += `- **${stat}:** ${value}\n`;
      });
      content += `\n`;
    }

    content += `## Impact\n\n`;
    content += `This result will have implications for both teams' standings and playoff positioning in the ${game.league}.\n\n`;

    content += `*Recap generated on ${new Date().toLocaleString()}*\n`;

    return content;
  }

  /**
   * Generate player news content
   */
  async generatePlayerNews(playerUpdate: { playerID: string; type: string; data: any }): Promise<string> {
    const { type, data } = playerUpdate;
    const playerName = data.playerName || 'Unknown Player';
    const team = data.team || 'Unknown Team';
    const date = new Date(data.date).toLocaleDateString();

    let content = `# Player Update: ${playerName}\n\n`;
    content += `**Player:** ${playerName}\n`;
    content += `**Team:** ${team}\n`;
    content += `**Date:** ${date}\n`;
    content += `**Update Type:** ${type.charAt(0).toUpperCase() + type.slice(1)}\n\n`;

    switch (type) {
      case 'injury':
        content += `## Injury Report\n\n`;
        content += `${playerName} has sustained an injury that may affect their availability for upcoming games. `;
        content += `The team medical staff is evaluating the situation and will provide updates on the player's status.\n\n`;
        break;

      case 'performance':
        content += `## Performance Highlight\n\n`;
        content += `${playerName} delivered an outstanding performance in their recent game. `;
        if (data.stats && Object.keys(data.stats).length > 0) {
          content += `Key statistics from the performance:\n\n`;
          Object.entries(data.stats).forEach(([stat, value]) => {
            content += `- **${stat}:** ${value}\n`;
          });
          content += `\n`;
        }
        break;

      case 'transaction':
        content += `## Team Transaction\n\n`;
        content += `${playerName} is involved in a team transaction that will impact their playing status. `;
        content += `This move reflects the team's strategic planning for the current and upcoming seasons.\n\n`;
        break;

      default:
        content += `## General Update\n\n`;
        content += `${playerName} has been in the news recently. `;
        if (data.description) {
          content += `${data.description}\n\n`;
        }
    }

    if (data.event) {
      content += `**Event Details:** ${data.event}\n\n`;
    }

    content += `## Analysis\n\n`;
    content += `This development will be closely monitored by fans and fantasy sports players alike. `;
    content += `The impact on ${team}'s performance and ${playerName}'s season outlook will become clearer in the coming days.\n\n`;

    content += `*Update generated on ${new Date().toLocaleString()}*\n`;

    return content;
  }

  /**
   * Generate team weekly recap
   */
  async generateTeamWeeklyRecap(team: Team): Promise<string> {
    const weekStart = new Date();
    weekStart.setDate(weekStart.getDate() - 7);

    let content = `# Weekly Team Recap: ${team.city} ${team.name}\n\n`;
    content += `**Team:** ${team.city} ${team.name}\n`;
    content += `**League:** ${team.league}\n`;
    content += `**Week Ending:** ${new Date().toLocaleDateString()}\n\n`;

    if (team.record) {
      content += `**Current Record:** ${team.record.wins}-${team.record.losses}`;
      if (team.record.ties) content += `-${team.record.ties}`;
      content += `\n\n`;
    }

    content += `## Week in Review\n\n`;

    if (team.recentGames && team.recentGames.length > 0) {
      content += `### Recent Games\n\n`;
      team.recentGames.slice(0, 3).forEach((game, index) => {
        const opponent = game.team1Name === team.name 
          ? `${game.team2City} ${game.team2Name}`
          : `${game.team1City} ${game.team1Name}`;
        const result = this.getGameResult(game, team);
        content += `${index + 1}. vs ${opponent} - ${result}\n`;
      });
      content += `\n`;
    } else {
      content += `The ${team.city} ${team.name} had a quiet week with no completed games.\n\n`;
    }

    if (team.upcomingGames && team.upcomingGames.length > 0) {
      content += `### Upcoming Games\n\n`;
      team.upcomingGames.slice(0, 3).forEach((game, index) => {
        const opponent = game.team1Name === team.name 
          ? `${game.team2City} ${game.team2Name}`
          : `${game.team1City} ${game.team1Name}`;
        const date = new Date(game.date).toLocaleDateString();
        content += `${index + 1}. vs ${opponent} - ${date}\n`;
      });
      content += `\n`;
    }

    if (team.keyPlayers && team.keyPlayers.length > 0) {
      content += `### Key Players This Week\n\n`;
      team.keyPlayers.slice(0, 3).forEach((player, index) => {
        content += `${index + 1}. **${player.name}** (${player.position})\n`;
      });
      content += `\n`;
    }

    if (team.trends) {
      content += `### Team Trends\n\n`;
      Object.entries(team.trends).forEach(([trend, value]) => {
        content += `- **${trend}:** ${value}\n`;
      });
      content += `\n`;
    }

    content += `## Outlook\n\n`;
    content += `The ${team.city} ${team.name} continues to develop their season strategy. `;
    content += `Upcoming games will be crucial for their positioning in the ${team.league} standings.\n\n`;

    content += `*Recap generated on ${new Date().toLocaleString()}*\n`;

    return content;
  }

  /**
   * Generate league statistics content
   */
  async generateLeagueStats(stats: LeagueStats): Promise<string> {
    let content = `# League Statistics Summary\n\n`;
    content += `**Sport:** ${stats.sport}\n`;
    content += `**League:** ${stats.league}\n`;
    content += `**Generated:** ${new Date().toLocaleString()}\n\n`;

    if (stats.topPerformers && stats.topPerformers.length > 0) {
      content += `## Top Performers\n\n`;
      stats.topPerformers.forEach(category => {
        content += `### ${category.category}\n\n`;
        category.players.slice(0, 5).forEach((performer, index) => {
          content += `${index + 1}. **${performer.player.name}** (${performer.player.team}) - ${performer.value}\n`;
        });
        content += `\n`;
      });
    }

    if (stats.teamStandings && stats.teamStandings.length > 0) {
      content += `## Team Standings\n\n`;
      stats.teamStandings.slice(0, 10).forEach((team, index) => {
        const record = team.record ? `${team.record.wins}-${team.record.losses}` : 'N/A';
        content += `${index + 1}. **${team.city} ${team.name}** - ${record}\n`;
      });
      content += `\n`;
    }

    if (stats.trends && Object.keys(stats.trends).length > 0) {
      content += `## League Trends\n\n`;
      Object.entries(stats.trends).forEach(([trend, value]) => {
        content += `- **${trend}:** ${value}\n`;
      });
      content += `\n`;
    }

    content += `## Analysis\n\n`;
    content += `The current statistics reflect the competitive landscape across ${stats.league}. `;
    content += `These numbers will continue to evolve as the season progresses and more games are completed.\n\n`;

    content += `*Statistics compiled on ${new Date().toLocaleString()}*\n`;

    return content;
  }

  /**
   * Generate betting content
   */
  async generateBettingContent(bettingData: BettingData): Promise<string> {
    let content = `# Weekly Betting Insights\n\n`;
    content += `**Generated:** ${new Date().toLocaleString()}\n\n`;

    if (bettingData.bestBets && bettingData.bestBets.length > 0) {
      content += `## Best Bets of the Week\n\n`;
      bettingData.bestBets.forEach((bet, index) => {
        content += `### ${index + 1}. ${bet.game.team1City} ${bet.game.team1Name} vs ${bet.game.team2City} ${bet.game.team2Name}\n\n`;
        content += `**Recommendation:** ${bet.recommendation}\n`;
        content += `**Confidence:** ${bet.confidence}%\n`;
        content += `**Reasoning:** ${bet.reasoning}\n`;
        content += `**Game Date:** ${new Date(bet.game.date).toLocaleDateString()}\n\n`;
      });
    }

    if (bettingData.upsetAlerts && bettingData.upsetAlerts.length > 0) {
      content += `## Upset Alerts\n\n`;
      bettingData.upsetAlerts.forEach((alert, index) => {
        content += `### ${index + 1}. ${alert.underdog} Upset Potential\n\n`;
        content += `**Game:** ${alert.game.team1City} ${alert.game.team1Name} vs ${alert.game.team2City} ${alert.game.team2Name}\n`;
        content += `**Underdog:** ${alert.underdog}\n`;
        content += `**Alert Reason:** ${alert.reasoning}\n`;
        content += `**Game Date:** ${new Date(alert.game.date).toLocaleDateString()}\n\n`;
      });
    }

    if (bettingData.trendAnalysis && Object.keys(bettingData.trendAnalysis).length > 0) {
      content += `## Betting Trends Analysis\n\n`;
      Object.entries(bettingData.trendAnalysis).forEach(([trend, value]) => {
        content += `- **${trend}:** ${value}\n`;
      });
      content += `\n`;
    }

    content += `## Disclaimer\n\n`;
    content += `All betting insights are for entertainment purposes only. Please gamble responsibly and within your means. `;
    content += `Past performance does not guarantee future results.\n\n`;

    content += `*Insights generated on ${new Date().toLocaleString()}*\n`;

    return content;
  }

  /**
   * Generate niche content
   */
  async generateNicheContent(nicheData: NicheData): Promise<string> {
    let content = `# Weekly Sports Stories & Trends\n\n`;
    content += `**Generated:** ${new Date().toLocaleString()}\n\n`;

    if (nicheData.playersByLocation && Object.keys(nicheData.playersByLocation).length > 0) {
      content += `## Players by Location\n\n`;
      Object.entries(nicheData.playersByLocation).forEach(([location, players]) => {
        if (players.length > 0) {
          content += `### ${location}\n\n`;
          players.slice(0, 3).forEach((player: any, index: number) => {
            content += `${index + 1}. **${player.name}** - ${player.recentEvent}\n`;
          });
          content += `\n`;
        }
      });
    }

    if (nicheData.milestoneTrackers && nicheData.milestoneTrackers.length > 0) {
      content += `## Milestone Watch\n\n`;
      nicheData.milestoneTrackers.forEach((tracker, index) => {
        content += `### ${index + 1}. ${tracker.player.name} - ${tracker.milestone}\n\n`;
        content += `**Progress:** ${tracker.progress}%\n`;
        content += `**Team:** ${tracker.player.team}\n\n`;
      });
    }

    if (nicheData.homeAwayTrends && Object.keys(nicheData.homeAwayTrends).length > 0) {
      content += `## Home vs Away Trends\n\n`;
      Object.entries(nicheData.homeAwayTrends).forEach(([trend, value]) => {
        content += `- **${trend}:** ${value}\n`;
      });
      content += `\n`;
    }

    if (nicheData.draftAnalysis && Object.keys(nicheData.draftAnalysis).length > 0) {
      content += `## Draft Analysis\n\n`;
      Object.entries(nicheData.draftAnalysis).forEach(([metric, value]) => {
        content += `- **${metric}:** ${value}\n`;
      });
      content += `\n`;
    }

    if (nicheData.rookieUpdates && nicheData.rookieUpdates.length > 0) {
      content += `## Rookie Watch\n\n`;
      nicheData.rookieUpdates.slice(0, 5).forEach((rookie, index) => {
        content += `${index + 1}. **${rookie.name}** (${rookie.team}) - ${rookie.position}\n`;
      });
      content += `\n`;
    }

    content += `## Fun Facts\n\n`;
    content += `- Sports continue to provide endless storylines and statistical intrigue\n`;
    content += `- Player connections to their hometowns remain a compelling narrative\n`;
    content += `- Milestone achievements add excitement to every game\n\n`;

    content += `*Stories compiled on ${new Date().toLocaleString()}*\n`;

    return content;
  }

  /**
   * Get game result for a team
   */
  private getGameResult(game: Game, team: Team): string {
    const isHome = game.team1Name === team.name;
    const teamScore = isHome ? game.team1Score : game.team2Score;
    const opponentScore = isHome ? game.team2Score : game.team1Score;

    if (teamScore === undefined || opponentScore === undefined) {
      return 'Game not completed';
    }

    const result = teamScore > opponentScore ? 'W' : 'L';
    return `${result} ${teamScore}-${opponentScore}`;
  }
}

content_engine/schedulers/ContentSchedul.content:
// Content Scheduler - Handles automated content generation

import { SportsContentEngine } from '../index';
import { Logger } from '../utils/Logger';

export class ContentScheduler {
  private contentEngine: SportsContentEngine;
  private logger: Logger;
  private intervals: Map<string, NodeJS.Timeout>;

  constructor(contentEngine: SportsContentEngine) {
    this.contentEngine = contentEngine;
    this.logger = new Logger();
    this.intervals = new Map();
  }

  /**
   * Start all scheduled content generation
   */
  startScheduledGeneration(): void {
    this.logger.info('Starting content generation schedules...');

    // Generate daily content every 6 hours
    this.scheduleDaily();

    // Generate weekly content every Sunday at midnight
    this.scheduleWeekly();

    // Generate player news every 2 hours
    this.schedulePlayerNews();

    this.logger.info('All content schedules started successfully');
  }

  /**
   * Stop all scheduled content generation
   */
  stopScheduledGeneration(): void {
    this.logger.info('Stopping all content generation schedules...');

    this.intervals.forEach((interval, name) => {
      clearInterval(interval);
      this.logger.info(`Stopped schedule: ${name}`);
    });

    this.intervals.clear();
    this.logger.info('All schedules stopped');
  }

  /**
   * Schedule daily content generation
   */
  private scheduleDaily(): void {
    const interval = setInterval(async () => {
      try {
        this.logger.info('Running scheduled daily content generation...');
        await this.contentEngine.generateDailyContent();
        this.logger.info('Scheduled daily content generation completed');
      } catch (error) {
        this.logger.error('Error in scheduled daily content generation:', error);
      }
    }, 6 * 60 * 60 * 1000); // Every 6 hours

    this.intervals.set('daily', interval);
    this.logger.info('Daily content schedule started (every 6 hours)');
  }

  /**
   * Schedule weekly content generation
   */
  private scheduleWeekly(): void {
    const interval = setInterval(async () => {
      const now = new Date();
      // Run on Sundays at midnight
      if (now.getDay() === 0 && now.getHours() === 0) {
        try {
          this.logger.info('Running scheduled weekly content generation...');
          await this.contentEngine.generateWeeklyContent();
          this.logger.info('Scheduled weekly content generation completed');
        } catch (error) {
          this.logger.error('Error in scheduled weekly content generation:', error);
        }
      }
    }, 60 * 60 * 1000); // Check every hour

    this.intervals.set('weekly', interval);
    this.logger.info('Weekly content schedule started (Sundays at midnight)');
  }

  /**
   * Schedule player news generation
   */
  private schedulePlayerNews(): void {
    const interval = setInterval(async () => {
      try {
        this.logger.info('Running scheduled player news generation...');
        // Generate only player news updates
        const playerUpdates = await this.contentEngine['dataService'].getPlayerUpdates();
        for (const update of playerUpdates) {
          const news = await this.contentEngine['contentGenerator'].generatePlayerNews(update);
          await this.contentEngine['saveContent']('player_news', `player_${update.playerID}_${Date.now()}`, news);
        }
        this.logger.info('Scheduled player news generation completed');
      } catch (error) {
        this.logger.error('Error in scheduled player news generation:', error);
      }
    }, 2 * 60 * 60 * 1000); // Every 2 hours

    this.intervals.set('player_news', interval);
    this.logger.info('Player news schedule started (every 2 hours)');
  }

  /**
   * Run immediate content generation
   */
  async runImmediateGeneration(type: 'daily' | 'weekly' | 'all' = 'all'): Promise<void> {
    try {
      this.logger.info(`Running immediate ${type} content generation...`);

      if (type === 'daily' || type === 'all') {
        await this.contentEngine.generateDailyContent();
      }

      if (type === 'weekly' || type === 'all') {
        await this.contentEngine.generateWeeklyContent();
      }

      this.logger.info(`Immediate ${type} content generation completed`);
    } catch (error) {
      this.logger.error(`Error in immediate ${type} content generation:`, error);
      throw error;
    }
  }

  /**
   * Get scheduler status
   */
  getStatus(): { [key: string]: boolean } {
    return {
      daily: this.intervals.has('daily'),
      weekly: this.intervals.has('weekly'),
      player_news: this.intervals.has('player_news')
    };
  }
}

content_engine/services/DataService.ts.content:
// Data Service - Handles all sports data fetching and processing

import { Game, Player, Team, LeagueStats, BettingData, NicheData } from '../types/ContentTypes';
import { Logger } from '../utils/Logger';

export class DataService {
  private logger: Logger;
  private apiKey: string;
  private baseUrl: string;

  constructor() {
    this.logger = new Logger();
    this.apiKey = process.env.SPORTS_API_KEY || '3e8b23fdd1b6030714b9320484d7367b';
    this.baseUrl = 'https://sharpshot.api.areyouwatchingthis.com/api';
  }

  /**
   * Get upcoming games for previews
   */
  async getUpcomingGames(days: number = 1): Promise<Game[]> {
    try {
      const response = await fetch(`${this.baseUrl}/games.json?apiKey=${this.apiKey}`);

      if (!response.ok) {
        throw new Error(`API request failed: ${response.status}`);
      }

      const data = await response.json();
      const games = data.results || [];

      // Filter for upcoming games
      const now = new Date();
      const upcoming = games.filter((game: any) => {
        const gameDate = new Date(game.date);
        return gameDate > now && game.status === 'scheduled';
      }).slice(0, 10); // Limit to 10 games

      return upcoming.map(this.mapGameData);
    } catch (error) {
      this.logger.error('Error fetching upcoming games:', error);
      return [];
    }
  }

  /**
   * Get completed games for recaps
   */
  async getCompletedGames(): Promise<Game[]> {
    try {
      const response = await fetch(`${this.baseUrl}/games.json?apiKey=${this.apiKey}`);

      if (!response.ok) {
        throw new Error(`API request failed: ${response.status}`);
      }

      const data = await response.json();
      const games = data.results || [];

      // Filter for completed games from today
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      const completed = games.filter((game: any) => {
        const gameDate = new Date(game.date);
        return gameDate >= today && 
               (game.status === 'final' || game.status === 'completed') &&
               game.team1Score !== undefined && 
               game.team2Score !== undefined;
      }).slice(0, 5); // Limit to 5 games

      return completed.map(this.mapGameData);
    } catch (error) {
      this.logger.error('Error fetching completed games:', error);
      return [];
    }
  }

  /**
   * Get player updates and news
   */
  async getPlayerUpdates(): Promise<Array<{ playerID: string; type: string; data: any }>> {
    try {
      const response = await fetch(`${this.baseUrl}/events.json?apiKey=${this.apiKey}&count=50`);

      if (!response.ok) {
        throw new Error(`API request failed: ${response.status}`);
      }

      const data = await response.json();
      const events = data.results || [];

      // Process events for player-related updates
      const playerUpdates = events
        .filter((event: any) => event.player || event.playerName)
        .map((event: any) => ({
          playerID: event.playerID || `${event.playerName}_${event.team}`,
          type: this.categorizePlayerEvent(event),
          data: {
            playerName: event.playerName || event.player,
            team: event.team,
            event: event.event,
            description: event.description,
            date: event.date,
            gameID: event.gameID,
            stats: event.stats || {}
          }
        }))
        .slice(0, 10); // Limit to 10 updates

      return playerUpdates;
    } catch (error) {
      this.logger.error('Error fetching player updates:', error);
      return [];
    }
  }

  /**
   * Get league-wide statistics
   */
  async getLeagueStats(): Promise<LeagueStats> {
    try {
      const gamesResponse = await fetch(`${this.baseUrl}/games.json?apiKey=${this.apiKey}`);
      const eventsResponse = await fetch(`${this.baseUrl}/events.json?apiKey=${this.apiKey}&count=100`);

      if (!gamesResponse.ok || !eventsResponse.ok) {
        throw new Error('Failed to fetch league data');
      }

      const gamesData = await gamesResponse.json();
      const eventsData = await eventsResponse.json();

      const games = gamesData.results || [];
      const events = eventsData.results || [];

      // Process stats from events
      const playerStats = this.processPlayerStats(events);
      const teamStats = this.processTeamStats(games);

      return {
        sport: 'Multiple',
        league: 'All Leagues',
        topPerformers: [
          {
            category: 'Most Active Players',
            players: playerStats.slice(0, 10)
          }
        ],
        teamStandings: teamStats.slice(0, 20),
        trends: {
          totalGames: games.length,
          totalEvents: events.length,
          activeLeagues: Array.from(new Set(games.map((g: any) => g.league))).length
        }
      };
    } catch (error) {
      this.logger.error('Error fetching league stats:', error);
      return {
        sport: 'Multiple',
        league: 'All Leagues',
        topPerformers: [],
        teamStandings: [],
        trends: {}
      };
    }
  }

  /**
   * Get all teams
   */
  async getAllTeams(): Promise<Team[]> {
    try {
      const response = await fetch(`${this.baseUrl}/games.json?apiKey=${this.apiKey}`);

      if (!response.ok) {
        throw new Error(`API request failed: ${response.status}`);
      }

      const data = await response.json();
      const games = data.results || [];

      // Extract unique teams from games
      const teamsMap = new Map<string, Team>();

      games.forEach((game: any) => {
        const team1Key = `${game.team1City}_${game.team1Name}`;
        const team2Key = `${game.team2City}_${game.team2Name}`;

        if (!teamsMap.has(team1Key)) {
          teamsMap.set(team1Key, {
            id: team1Key,
            name: game.team1Name,
            city: game.team1City,
            sport: game.sport,
            league: game.league
          });
        }

        if (!teamsMap.has(team2Key)) {
          teamsMap.set(team2Key, {
            id: team2Key,
            name: game.team2Name,
            city: game.team2City,
            sport: game.sport,
            league: game.league
          });
        }
      });

      return Array.from(teamsMap.values()).slice(0, 20); // Limit to 20 teams
    } catch (error) {
      this.logger.error('Error fetching teams:', error);
      return [];
    }
  }

  /**
   * Get betting data and insights
   */
  async getBettingData(): Promise<BettingData> {
    try {
      const games = await this.getUpcomingGames(7); // Next 7 days

      const bestBets = games
        .filter(game => game.odds)
        .map(game => ({
          game,
          betType: 'spread',
          recommendation: `Take ${game.team1City} ${game.team1Name} ${game.odds!.spread > 0 ? '+' : ''}${game.odds!.spread}`,
          confidence: Math.floor(Math.random() * 30) + 70, // 70-100% confidence
          reasoning: this.generateBettingReasoning(game)
        }))
        .slice(0, 5);

      const upsetAlerts = games
        .filter(game => game.odds && Math.abs(game.odds.spread) > 7)
        .map(game => ({
          game,
          underdog: game.odds!.spread > 0 ? `${game.team1City} ${game.team1Name}` : `${game.team2City} ${game.team2Name}`,
          reasoning: `Large spread of ${Math.abs(game.odds!.spread)} points creates upset potential`
        }))
        .slice(0, 3);

      return {
        bestBets,
        upsetAlerts,
        trendAnalysis: {
          totalGamesAnalyzed: games.length,
          avgSpread: games.reduce((sum, g) => sum + (g.odds?.spread || 0), 0) / games.length,
          highConfidenceBets: bestBets.filter(bet => bet.confidence > 85).length
        }
      };
    } catch (error) {
      this.logger.error('Error generating betting data:', error);
      return {
        bestBets: [],
        upsetAlerts: [],
        trendAnalysis: {}
      };
    }
  }

  /**
   * Get niche and thematic data
   */
  async getNicheData(): Promise<NicheData> {
    try {
      const events = await this.getPlayerUpdates();
      const teams = await this.getAllTeams();

      // Group players by location (using team city as proxy)
      const playersByLocation: Record<string, any[]> = {};
      events.forEach(update => {
        const location = update.data.team || 'Unknown';
        if (!playersByLocation[location]) {
          playersByLocation[location] = [];
        }
        playersByLocation[location].push({
          name: update.data.playerName,
          team: update.data.team,
          recentEvent: update.data.event
        });
      });

      // Generate milestone trackers
      const milestoneTrackers = events
        .filter(update => update.type === 'performance')
        .map(update => ({
          player: {
            playerID: update.playerID,
            name: update.data.playerName,
            team: update.data.team,
            position: 'Unknown',
            stats: update.data.stats || {}
          },
          milestone: 'Performance Milestone',
          progress: Math.floor(Math.random() * 100)
        }))
        .slice(0, 5);

      return {
        playersByLocation,
        draftAnalysis: {
          totalTeams: teams.length,
          leaguesRepresented: Array.from(new Set(teams.map(t => t.league))).length
        },
        milestoneTrackers,
        homeAwayTrends: {
          totalHomeGames: Math.floor(Math.random() * 100),
          totalAwayGames: Math.floor(Math.random() * 100),
          homeWinPercentage: Math.random() * 0.4 + 0.4 // 40-80%
        },
        rookieUpdates: []
      };
    } catch (error) {
      this.logger.error('Error fetching niche data:', error);
      return {
        playersByLocation: {},
        draftAnalysis: {},
        milestoneTrackers: [],
        homeAwayTrends: {},
        rookieUpdates: []
      };
    }
  }

  /**
   * Map raw game data to Game interface
   */
  private mapGameData = (gameData: any): Game => ({
    gameID: gameData.gameID || String(Math.random()),
    sport: gameData.sport || 'Unknown',
    league: gameData.league || 'Unknown',
    date: gameData.date,
    time: gameData.time || '',
    status: gameData.status || 'scheduled',
    team1Name: gameData.team1Name,
    team1City: gameData.team1City,
    team1Score: gameData.team1Score,
    team2Name: gameData.team2Name,
    team2City: gameData.team2City,
    team2Score: gameData.team2Score,
    venue: gameData.venue,
    weather: gameData.weather,
    odds: gameData.odds ? {
      spread: parseFloat(gameData.odds.spread) || 0,
      overUnder: parseFloat(gameData.odds.overUnder) || 0,
      moneyline1: parseFloat(gameData.odds.moneyline1) || 0,
      moneyline2: parseFloat(gameData.odds.moneyline2) || 0
    } : undefined
  });

  /**
   * Categorize player event type
   */
  private categorizePlayerEvent(event: any): string {
    const eventText = (event.event || event.description || '').toLowerCase();

    if (eventText.includes('injury') || eventText.includes('hurt')) return 'injury';
    if (eventText.includes('goal') || eventText.includes('touchdown') || eventText.includes('point')) return 'performance';
    if (eventText.includes('trade') || eventText.includes('sign')) return 'transaction';

    return 'general';
  }

  /**
   * Process player statistics from events
   */
  private processPlayerStats(events: any[]): Array<{ player: any; value: number }> {
    const playerCounts: Record<string, number> = {};

    events.forEach(event => {
      const playerName = event.playerName || event.player;
      if (playerName) {
        playerCounts[playerName] = (playerCounts[playerName] || 0) + 1;
      }
    });

    return Object.entries(playerCounts)
      .map(([name, count]) => ({
        player: {
          playerID: name,
          name,
          team: 'Unknown',
          position: 'Unknown',
          stats: { events: count }
        },
        value: count
      }))
      .sort((a, b) => b.value - a.value);
  }

  /**
   * Process team statistics from games
   */
  private processTeamStats(games: any[]): Team[] {
    const teamStats: Record<string, { wins: number; losses: number; games: number }> = {};

    games
      .filter(game => game.team1Score !== undefined && game.team2Score !== undefined)
      .forEach(game => {
        const team1Key = `${game.team1City}_${game.team1Name}`;
        const team2Key = `${game.team2City}_${game.team2Name}`;

        if (!teamStats[team1Key]) teamStats[team1Key] = { wins: 0, losses: 0, games: 0 };
        if (!teamStats[team2Key]) teamStats[team2Key] = { wins: 0, losses: 0, games: 0 };

        teamStats[team1Key].games++;
        teamStats[team2Key].games++;

        if (game.team1Score > game.team2Score) {
          teamStats[team1Key].wins++;
          teamStats[team2Key].losses++;
        } else {
          teamStats[team2Key].wins++;
          teamStats[team1Key].losses++;
        }
      });

    return Object.entries(teamStats).map(([teamKey, stats]) => {
      const [city, name] = teamKey.split('_');
      return {
        id: teamKey,
        name,
        city,
        sport: 'Unknown',
        league: 'Unknown',
        record: {
          wins: stats.wins,
          losses: stats.losses
        }
      };
    });
  }

  /**
   * Generate betting reasoning
   */
  private generateBettingReasoning(game: Game): string {
    const reasons = [
      `${game.team1City} has strong recent form`,
      `Home field advantage favors the spread`,
      `Key matchup advantages in this game`,
      `Weather conditions favor the under`,
      `Recent head-to-head trends support this pick`,
      `Injury reports favor this side`,
      `Statistical trends align with this bet`
    ];

    return reasons[Math.floor(Math.random() * reasons.length)];
  }
}

content_engine/types/ContentTypes.ts.content:
// Content Types and Interfaces for Sports Content Engine

export enum ContentTypes {
  GAME_PREVIEW = 'game_preview',
  GAME_RECAP = 'game_recap',
  PLAYER_NEWS = 'player_news',
  TEAM_WEEKLY = 'team_weekly',
  LEAGUE_STATS = 'league_stats',
  BETTING_INSIGHTS = 'betting_insights',
  NICHE_STORIES = 'niche_stories'
}

export interface Game {
  gameID: string;
  sport: string;
  league: string;
  date: string;
  time: string;
  status: string;
  team1Name: string;
  team1City: string;
  team1Score?: number;
  team2Name: string;
  team2City: string;
  team2Score?: number;
  venue?: string;
  weather?: string;
  odds?: {
    spread: number;
    overUnder: number;
    moneyline1: number;
    moneyline2: number;
  };
  keyStats?: Record<string, any>;
  highlights?: string[];
}

export interface Player {
  playerID: string;
  name: string;
  team: string;
  position: string;
  stats: Record<string, number>;
  injuryStatus?: string;
  recentPerformance?: Performance[];
  milestones?: Milestone[];
}

export interface Performance {
  gameID: string;
  date: string;
  stats: Record<string, number>;
  highlights: string[];
}

export interface Milestone {
  type: string;
  description: string;
  date: string;
  value: number;
}

export interface Team {
  id: string;
  name: string;
  city: string;
  sport: string;
  league: string;
  record?: {
    wins: number;
    losses: number;
    ties?: number;
  };
  recentGames?: Game[];
  upcomingGames?: Game[];
  keyPlayers?: Player[];
  trends?: Record<string, any>;
}

export interface LeagueStats {
  sport: string;
  league: string;
  topPerformers: {
    category: string;
    players: Array<{
      player: Player;
      value: number;
    }>;
  }[];
  teamStandings: Team[];
  trends: Record<string, any>;
}

export interface BettingData {
  bestBets: Array<{
    game: Game;
    betType: string;
    recommendation: string;
    confidence: number;
    reasoning: string;
  }>;
  upsetAlerts: Array<{
    game: Game;
    underdog: string;
    reasoning: string;
  }>;
  trendAnalysis: Record<string, any>;
}

export interface NicheData {
  playersByLocation: Record<string, Player[]>;
  draftAnalysis: Record<string, any>;
  milestoneTrackers: Array<{
    player: Player;
    milestone: string;
    progress: number;
  }>;
  homeAwayTrends: Record<string, any>;
  rookieUpdates: Player[];
}

export interface ContentTemplate {
  type: ContentTypes;
  title: string;
  sections: string[];
  requiredData: string[];
}

export interface GeneratedContent {
  id: string;
  type: ContentTypes;
  title: string;
  content: string;
  metadata: {
    generatedAt: Date;
    dataSource: string;
    gameIds?: string[];
    playerIds?: string[];
    teamIds?: string[];
  };
  tags: string[];
}

content_engine/utils/Logger.ts.content:
// Logger utility for content engine

export class Logger {
  private logLevel: 'debug' | 'info' | 'warn' | 'error';

  constructor(logLevel: 'debug' | 'info' | 'warn' | 'error' = 'info') {
    this.logLevel = logLevel;
  }

  debug(message: string, ...args: any[]): void {
    if (this.shouldLog('debug')) {
      console.debug(`[CONTENT-ENGINE-DEBUG] ${new Date().toISOString()} - ${message}`, ...args);
    }
  }

  info(message: string, ...args: any[]): void {
    if (this.shouldLog('info')) {
      console.info(`[CONTENT-ENGINE-INFO] ${new Date().toISOString()} - ${message}`, ...args);
    }
  }

  warn(message: string, ...args: any[]): void {
    if (this.shouldLog('warn')) {
      console.warn(`[CONTENT-ENGINE-WARN] ${new Date().toISOString()} - ${message}`, ...args);
    }
  }

  error(message: string, ...args: any[]): void {
    if (this.shouldLog('error')) {
      console.error(`[CONTENT-ENGINE-ERROR] ${new Date().toISOString()} - ${message}`, ...args);
    }
  }

  private shouldLog(level: 'debug' | 'info' | 'warn' | 'error'): boolean {
    const levels = ['debug', 'info', 'warn', 'error'];
    const currentLevelIndex = levels.indexOf(this.logLevel);
    const messageLevelIndex = levels.indexOf(level);
    return messageLevelIndex >= currentLevelIndex;
  }
}

