    'BetRivers': 'bg-cyan-600',
    'SuperDraft': 'bg-amber-500',
    'PrizePicks': 'bg-purple-500',
    'Underdog': 'bg-emerald-600',
    'Bet365': 'bg-yellow-400',
    'William Hill': 'bg-blue-700',
    'Betway': 'bg-green-600',
    'Hard Rock': 'bg-gray-600',
    'ESPN BET': 'bg-red-600',
    'Fliff': 'bg-pink-500'
