                    name="subject"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="text-sm font-medium">Subject *</FormLabel>
                        <FormControl>
                          <Input 
                            {...field} 
                            placeholder="One line that sums it up"
