                          onClick={() => setActiveSport("all")}
                        >
                          ALL
                        </Badge>
                        <Badge 
                          variant={activeSport === "nfl" ? "default" : "secondary"}
                          className={`cursor-pointer font-mono text-xs ${activeSport === "nfl" ? 'bg-[#D8AC35] text-white dark:bg-[#D8AC35] dark:text-black' : 'bg-white/80 text-gray-700 border-gray-300 dark:bg-gray-800/80 dark:text-white dark:border-gray-600 backdrop-blur-sm'}`}
                          onClick={() => setActiveSport("nfl")}
                        >
                          NFL
                        </Badge>
                        <Badge 
                          variant={activeSport === "nba" ? "default" : "secondary"}
                          className={`cursor-pointer font-mono text-xs ${activeSport === "nba" ? 'bg-[#D8AC35] text-white dark:bg-[#D8AC35] dark:text-black' : 'bg-white/80 text-gray-700 border-gray-300 dark:bg-gray-800/80 dark:text-white dark:border-gray-600 backdrop-blur-sm'}`}
                          onClick={() => setActiveSport("nba")}
                        >
                          NBA
                        </Badge>
                        <Badge 
                          variant={activeSport === "mlb" ? "default" : "secondary"}
                          className={`cursor-pointer font-mono text-xs ${activeSport === "mlb" ? 'bg-[#D8AC35] text-white dark:bg-[#D8AC35] dark:text-black' : 'bg-white/80 text-gray-700 border-gray-300 dark:bg-gray-800/80 dark:text-white dark:border-gray-600 backdrop-blur-sm'}`}
                          onClick={() => setActiveSport("mlb")}
                        >
                          MLB
                        </Badge>
                      </div>
                    </div>

                    <div className="space-y-3">
                      <div className="text-[#D8AC35] dark:text-[#D8AC35] text-sm font-mono uppercase tracking-wider mb-2">+EV THRESHOLD</div>
                      <div className="flex items-center space-x-4">
                        <div className="flex-1">
                          <Slider
                            value={[parseFloat(minEV)]}
                            onValueChange={(value) => setMinEV(value[0].toString())}
                            max={20}
                            min={0}
                            step={0.5}
                            className="w-full"
                          />
                        </div>
                        <span className="text-[#D8AC35] dark:text-[#D8AC35] font-mono text-lg font-bold min-w-16">+{minEV}%</span>
                      </div>
                    </div>

                    <div className="space-y-3">
                      <div className="text-[#D8AC35] dark:text-[#D8AC35] text-sm font-mono uppercase tracking-wider mb-2">STATUS</div>
                      <div className="flex items-center gap-3">
                        <div className="w-4 h-4 bg-[#D8AC35] dark:bg-[#D8AC35] rounded-full animate-pulse"></div>
                        <span className="text-[#D8AC35] dark:text-[#D8AC35] font-mono text-lg">SCANNING LIVE</span>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Trading Data Grid */}
                <div className="overflow-x-auto flex-1">
                  {isLoadingOpportunities ? (
                    <div className="flex flex-col items-center justify-center py-16">
                      <div className="relative">
                        <div className="w-16 h-16 border-4 border-gray-300 dark:border-gray-700 border-t-[#D8AC35] dark:border-t-[#D8AC35] rounded-full animate-spin"></div>
                        <div className="w-12 h-12 border-4 border-gray-200 dark:border-gray-800 border-t-[#D8AC35] dark:border-t-[#D8AC35] rounded-full animate-spin absolute top-2 left-2" style={{animationDirection: 'reverse', animationDuration: '1.5s'}}></div>
                      </div>
                      <div className="text-[#D8AC35] dark:text-[#D8AC35] font-mono text-lg font-bold mt-6 uppercase tracking-wider">SCANNING LIVE PRESETS</div>
                      <div className="text-gray-600 dark:text-gray-400 font-mono text-sm mt-2">Analyzing betting opportunities from {terminalStats?.booksScanned || 18} sportsbooks</div>
                    </div>
                  ) : (
                  <div className="min-w-[1400px] p-10">
                    {presetsData
                      .filter(preset => preset.ev >= parseFloat(minEV))
                      .sort((a, b) => b.ev - a.ev)
                      .map((preset) => (
                      <div key={preset.id} className="grid grid-cols-12 gap-4 items-center py-5 px-4 rounded-lg border-l-4 border-l-[#D8AC35] dark:border-l-[#D8AC35] bg-white/60 dark:bg-gray-900/30 hover:bg-white/80 dark:hover:bg-gray-900/50 transition-all duration-300 mb-4 backdrop-blur-sm">
