import React, { useState } from 'react';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Button } from '@/components/ui/button';
import { Slider } from '@/components/ui/slider';
import { Badge } from '@/components/ui/badge';
import { Search, RotateCcw, Filter } from 'lucide-react';

export interface FilterState {
  leagues: string[];
  markets: string[];
  livePreMatch: 'all' | 'live' | 'prematch';
  oddsRange: [number, number];
  minMaxOddsRange: [number, number];
  minimumDataPoints: number;
  myBooks: string[];
  evThreshold: number;
  search: string;
}

interface FilterBarProps {
  filters: FilterState;
  onFiltersChange: (filters: FilterState) => void;
  onReset: () => void;
  availableLeagues: string[];
  availableBooks: string[];
  className?: string;
}

const MARKET_OPTIONS = [
  { value: 'all', label: 'All Markets' },
  { value: 'moneyline', label: 'Moneyline' },
  { value: 'spread', label: 'Spread' },
  { value: 'total', label: 'Total' },
  { value: 'player_props', label: 'Player Props' }
];

export function FilterBar({ 
  filters, 
  onFiltersChange, 
  onReset, 
  availableLeagues, 
  availableBooks,
  className = ''
}: FilterBarProps) {
  const [showAdvanced, setShowAdvanced] = useState(false);

  const updateFilter = (key: keyof FilterState, value: any) => {
    onFiltersChange({ ...filters, [key]: value });
  };

  const toggleLeague = (league: string) => {
    const newLeagues = filters.leagues.includes(league)
      ? filters.leagues.filter(l => l !== league)
      : [...filters.leagues, league];
    updateFilter('leagues', newLeagues);
  };

  const toggleBook = (book: string) => {
    const newBooks = filters.myBooks.includes(book)
      ? filters.myBooks.filter(b => b !== book)
      : [...filters.myBooks, book];
    updateFilter('myBooks', newBooks);
  };

  return (
    <div className={`space-y-3 ${className}`}>
      {/* Primary Filters Row */}
      <div className="flex flex-wrap items-center gap-3 text-xs">
        {/* Search */}
        <div className="relative min-w-[180px]">
          <Search className="absolute left-2 top-1/2 transform -translate-y-1/2 h-3 w-3 text-zinc-500" />
          <Input
            placeholder="Search teams..."
            value={filters.search}
            onChange={(e) => updateFilter('search', e.target.value)}
            className="pl-7 h-7 text-xs bg-zinc-800/50 border-zinc-700 text-white placeholder:text-zinc-500 font-mono"
          />
        </div>

        {/* Market Selector */}
        <Select value={filters.markets[0] || 'all'} onValueChange={(value) => updateFilter('markets', [value])}>
          <SelectTrigger className="w-[120px] h-7 text-xs bg-zinc-800/50 border-zinc-700 text-white font-mono">
            <SelectValue />
          </SelectTrigger>
          <SelectContent className="bg-zinc-900 border-zinc-700">
            {MARKET_OPTIONS.map(option => (
              <SelectItem key={option.value} value={option.value} className="text-xs font-mono text-white hover:bg-zinc-800">
                {option.label}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>

        {/* Live/Prematch Toggle */}
        <Select value={filters.livePreMatch} onValueChange={(value: any) => updateFilter('livePreMatch', value)}>
          <SelectTrigger className="w-[100px] h-7 text-xs bg-zinc-800/50 border-zinc-700 text-white font-mono">
            <SelectValue />
          </SelectTrigger>
          <SelectContent className="bg-zinc-900 border-zinc-700">
            <SelectItem value="all" className="text-xs font-mono text-white hover:bg-zinc-800">All</SelectItem>
            <SelectItem value="live" className="text-xs font-mono text-white hover:bg-zinc-800">Live</SelectItem>
            <SelectItem value="prematch" className="text-xs font-mono text-white hover:bg-zinc-800">Pre-match</SelectItem>
          </SelectContent>
        </Select>

        {/* EV Threshold */}
        <div className="flex items-center gap-2 min-w-[160px]">
          <span className="text-xs text-zinc-400 font-mono">EV%:</span>
          <div className="flex-1 px-2">
            <Slider
              value={[filters.evThreshold]}
              onValueChange={(value) => updateFilter('evThreshold', value[0])}
              min={-20}
              max={100}
              step={0.1}
              className="w-full [&_[role=slider]]:cursor-grab [&_[role=slider]:active]:cursor-grabbing [&_[role=slider]]:h-3 [&_[role=slider]]:w-3 [&_.range]:h-1"
            />
          </div>
          <span className="text-xs font-mono min-w-[40px] text-white">
            {filters.evThreshold > 0 ? '+' : ''}{filters.evThreshold.toFixed(1)}%
          </span>
        </div>

        {/* Advanced Filters Toggle */}
        <Button
          variant="ghost"
          size="sm"
          onClick={() => setShowAdvanced(!showAdvanced)}
          className="text-muted-foreground hover:text-foreground"
        >
          <Filter className="h-4 w-4 mr-1" />
          {showAdvanced ? 'Hide' : 'More'}
        </Button>

        {/* Reset */}
        <Button
          variant="ghost"
          size="sm"
          onClick={onReset}
          className="text-muted-foreground hover:text-foreground"
        >
          <RotateCcw className="h-4 w-4 mr-1" />
          Reset
        </Button>
      </div>

      {/* Advanced Filters */}
      {showAdvanced && (
        <div className="space-y-4 pt-4 border-t">
          {/* Leagues */}
          <div>
            <label className="text-sm font-medium mb-2 block">Leagues</label>
            <div className="flex flex-wrap gap-2">
              {availableLeagues.map(league => (
                <Button
                  key={league}
                  variant={filters.leagues.includes(league) ? "default" : "outline"}
                  size="sm"
                  onClick={() => toggleLeague(league)}
                  className="h-7 px-2"
                >
                  {league.toUpperCase()}
                </Button>
              ))}
            </div>
          </div>

          {/* My Books */}
          <div>
            <label className="text-sm font-medium mb-2 block">My Books</label>
            <div className="flex flex-wrap gap-2">
              {availableBooks.slice(0, 10).map(book => (
                <Button
                  key={book}
                  variant={filters.myBooks.includes(book) ? "default" : "outline"}
                  size="sm"
                  onClick={() => toggleBook(book)}
                  className="h-7 px-2"
                >
                  {book}
                </Button>
              ))}
            </div>
          </div>

          {/* Odds Range */}
          <div>
            <label className="text-sm font-medium mb-2 block">Odds Range</label>
            <div className="flex items-center gap-4">
              <Input
                type="number"
                placeholder="Min"
                value={filters.oddsRange[0] === -Infinity ? '' : filters.oddsRange[0]}
                onChange={(e) => {
                  const val = e.target.value === '' ? -Infinity : Number(e.target.value);
                  updateFilter('oddsRange', [val, filters.oddsRange[1]]);
                }}
                className="w-24"
              />
              <span className="text-muted-foreground">to</span>
              <Input
                type="number"
                placeholder="Max"
                value={filters.oddsRange[1] === Infinity ? '' : filters.oddsRange[1]}
                onChange={(e) => {
                  const val = e.target.value === '' ? Infinity : Number(e.target.value);
                  updateFilter('oddsRange', [filters.oddsRange[0], val]);
                }}
                className="w-24"
              />
            </div>
          </div>

          {/* Min/Max Odds Range */}
          <div>
            <label className="text-sm font-medium mb-2 block">Min/Max Odds Filter</label>
            <div className="flex items-center gap-4">
              <Input
                type="number"
                placeholder="Min"
                value={filters.minMaxOddsRange[0] === -Infinity ? '' : filters.minMaxOddsRange[0]}
                onChange={(e) => {
                  const val = e.target.value === '' ? -Infinity : Number(e.target.value);
                  updateFilter('minMaxOddsRange', [val, filters.minMaxOddsRange[1]]);
                }}
                className="w-24"
              />
              <span className="text-muted-foreground">to</span>
              <Input
                type="number"
                placeholder="Max"
                value={filters.minMaxOddsRange[1] === Infinity ? '' : filters.minMaxOddsRange[1]}
                onChange={(e) => {
                  const val = e.target.value === '' ? Infinity : Number(e.target.value);
                  updateFilter('minMaxOddsRange', [filters.minMaxOddsRange[0], val]);
                }}
                className="w-24"
              />
            </div>
          </div>

          {/* Minimum Data Points */}
          <div>
            <label className="text-sm font-medium mb-2 block">Minimum Data Points</label>
            <div className="flex items-center gap-4">
              <Slider
                value={[filters.minimumDataPoints]}
                onValueChange={(value) => updateFilter('minimumDataPoints', value[0])}
                min={1}
                max={25}
                step={1}
                className="flex-1"
              />
              <span className="text-sm font-mono min-w-[40px] font-medium">
                {filters.minimumDataPoints}
              </span>
            </div>
            <p className="text-xs text-muted-foreground mt-1">
              Minimum number of sportsbooks required to show an opportunity
            </p>
          </div>
        </div>
      )}

      {/* Active Filters Summary */}
      {(filters.leagues.length > 0 || filters.myBooks.length > 0 || filters.search) && (
        <div className="flex flex-wrap items-center gap-2">
          <span className="text-sm text-muted-foreground">Active:</span>
          {filters.search && (
            <Badge variant="secondary" className="text-xs">
              Search: "{filters.search}"
            </Badge>
          )}
          {filters.leagues.map(league => (
            <Badge key={league} variant="secondary" className="text-xs">
              {league}
            </Badge>
          ))}
          {filters.myBooks.map(book => (
            <Badge key={book} variant="secondary" className="text-xs flex items-center gap-1">
              <span className="font-bold">
                {book.slice(0, 3).toUpperCase()}
              </span>
              <span>{book}</span>
            </Badge>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/components/trading/Opportunit.content:
import React from 'react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { Copy, Star, ExternalLink } from 'lucide-react';
import { toAmerican, toPercent, toRelTime, fmtMarket, getEVColor } from '@/lib/formatting';
import { BettingOpportunity } from './OpportunityTable';

interface OpportunityRowProps {
  opportunity: BettingOpportunity;
  onClick?: () => void;
  isEven?: boolean;
}

export function OpportunityRow({ opportunity, onClick, isEven = false }: OpportunityRowProps) {

  const handleCopyOdds = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (opportunity.myPrice) {
      navigator.clipboard.writeText(toAmerican(opportunity.myPrice.odds));
    }
  };

  const handleToggleWatchlist = (e: React.MouseEvent) => {
    e.stopPropagation();
    // TODO: Implement watchlist functionality
  };

  const handleExternalLink = (e: React.MouseEvent, url?: string) => {
    e.stopPropagation();
    if (url) {
      window.open(url, '_blank', 'noopener,noreferrer');
    }
  };

  const formatTimeChip = (startTime: string) => {
    try {
      const time = new Date(startTime);
      return time.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    } catch {
      return '‚Äî';
    }
  };

  const formatPropInfo = (opportunity: BettingOpportunity): string => {
    const { market } = opportunity;

    // For totals: "Under 0.5", "Over 6.5"
    if (market.type === 'total' && market.line !== undefined) {
      const sideText = market.side === 'over' ? 'Over' : 'Under';
      return `${sideText} ${market.line}`;
    }

    // For spreads with team: "San Diego Padres +3.5", "Chicago White Sox -6.5"
    if (market.type === 'spread' && market.line !== undefined) {
      const team = market.side === 'home' ? opportunity.event.home : opportunity.event.away;
      const sign = market.line >= 0 ? '+' : '';
      return `${team} ${sign}${market.line}`;
    }

    // For moneyline: show the team name "Kansas City Royals"
    if (market.type === 'moneyline') {
      const team = market.side === 'home' ? opportunity.event.home : opportunity.event.away;
      return team;
    }

    // For player props: "Player Name Over 12.5"
    if (market.type === 'player_prop' && market.player) {
      if (market.line !== undefined) {
        const sideText = market.side === 'over' ? 'Over' : market.side === 'under' ? 'Under' : market.side;
        return `${market.player} ${sideText} ${market.line}`;
      }
      return `${market.player} ${market.side || 'Prop'}`;
    }

    // Fallback for any unhandled cases
    if (market.line !== undefined) {
      const sideText = market.side === 'over' ? 'Over' : market.side === 'under' ? 'Under' : market.side;
      return `${sideText} ${market.line}`;
    }

    return market.side || market.type || 'Prop';
  };

  const formatMarketInfo = (opportunity: BettingOpportunity): string => {
    const { market } = opportunity;

    // For totals: "Total Points"
    if (market.type === 'total') {
      return 'Total Points';
    }

    // For spreads: "Point Spread"
    if (market.type === 'spread') {
      return 'Point Spread';
    }

    // For moneyline: "Moneyline"
    if (market.type === 'moneyline') {
      return 'Moneyline';
    }

    // For player props: show the stat type if available
    if (market.type === 'player_prop') {
      return 'Player Prop';
    }

    return market.type || 'Market';
  };

  const getCategoryLabel = (category: string) => {
    switch (category) {
      case 'ev': return '+EV';
      case 'arbitrage': return 'Arbitrage';
      case 'middling': return 'Middling';
      default: return category || 'N/A';
    }
  };

  const evPct = opportunity.evPercent || 0;
  const fairOdds = opportunity.fairOdds;
  const fairProb = opportunity.fairProbability;

  return (
    <TooltipProvider>
      <tr 
        className={`border-b border-border/30 hover:bg-muted/20 cursor-pointer transition-all duration-200 ${
          isEven ? 'bg-background' : 'bg-muted/5'
        }`}
        onClick={onClick}
      >
        {/* Category */}
        <td className="px-4 py-3">
          <div 
            className={`text-xs font-mono font-medium uppercase tracking-wider px-2 py-1 rounded ${
              opportunity.category === 'ev' ? 'text-green-400 bg-green-500/10 border border-green-500/20' :
              opportunity.category === 'arbitrage' ? 'text-purple-400 bg-purple-500/10 border border-purple-500/20' :
              opportunity.category === 'middling' ? 'text-blue-400 bg-blue-500/10 border border-blue-500/20' :
              'text-muted-foreground bg-muted/50 border border-border/50'
            }`}
          >
            {getCategoryLabel(opportunity.category)}
          </div>
        </td>

        {/* Event */}
        <td className="px-4 py-3">
          <div>
            <div className="font-medium text-sm text-foreground">
              {opportunity.event.away} vs {opportunity.event.home}
            </div>
            <div className="flex gap-1 mt-1">
              <div 
                className={`text-xs px-1.5 py-0.5 rounded font-mono font-medium ${
                  opportunity.event.status === 'live' 
                    ? 'text-red-400 bg-red-500/10 border border-red-500/20' 
                    : 'text-muted-foreground bg-muted/30 border border-border/30'
                }`}
              >
                {opportunity.event.status === 'live' ? 'LIVE' : 'PRE'}
              </div>
              <div className="text-xs px-1.5 py-0.5 rounded font-mono text-muted-foreground bg-muted/30 border border-border/30">
                {formatTimeChip(opportunity.event.startTime)}
              </div>
            </div>
          </div>
        </td>

        {/* League */}
        <td className="px-4 py-3">
          <div className="text-xs px-1.5 py-0.5 rounded font-mono text-muted-foreground bg-muted/30 border border-border/30">
            {opportunity.event.league}
          </div>
        </td>

        {/* Prop */}
        <td className="px-4 py-3">
          <div className="text-sm font-medium text-foreground">
            {formatPropInfo(opportunity)}
          </div>
        </td>

        {/* Market */}
        <td className="px-4 py-3">
          <div className="text-sm text-muted-foreground">
            {formatMarketInfo(opportunity)}
          </div>
        </td>

        {/* Hit % */}
        <td className="px-4 py-3 text-right">
          {fairProb ? (
            <span className="font-mono text-sm text-muted-foreground">
              {toPercent(fairProb)}
            </span>
          ) : (
            <span className="text-muted-foreground text-sm">‚Äî</span>
          )}
        </td>

        {/* +EV % */}
        <td className="px-4 py-3 text-right">
          <Tooltip>
            <TooltipTrigger>
              <span className={`font-mono text-sm font-bold ${getEVColor(evPct)}`}>
                {toPercent(evPct, true)}
              </span>
            </TooltipTrigger>
            <TooltipContent>
              <p>EV% = value of your selected book compared to fair odds. Red = negative, Yellow = neutral, Green = positive.</p>
            </TooltipContent>
          </Tooltip>
        </td>

        {/* My Odds */}
        <td className="px-4 py-3">
          {opportunity.myPrice ? (
            <Tooltip>
              <TooltipTrigger asChild>
                <div
                  className="inline-flex items-center h-6 px-2 text-xs font-mono bg-muted/30 border border-border/50 rounded cursor-pointer hover:bg-muted/50 transition-colors"
                  onClick={(e) => handleExternalLink(e, opportunity.myPrice.url)}
                >
                  <span className="text-muted-foreground text-xs">{opportunity.myPrice.book}</span>
                  <span className="ml-1 font-bold">{toAmerican(opportunity.myPrice.odds)}</span>
                  {opportunity.myPrice.url && <ExternalLink className="ml-1 h-3 w-3 text-muted-foreground" />}
                </div>
              </TooltipTrigger>
              <TooltipContent>
                <div className="space-y-1">
                  <p className="font-medium">My Book Price:</p>
                  <p className="text-xs">
                    {opportunity.myPrice.book}: {toAmerican(opportunity.myPrice.odds)}
                  </p>
                </div>
              </TooltipContent>
            </Tooltip>
          ) : (
            <span className="text-muted-foreground text-sm">‚Äî</span>
          )}
        </td>

        {/* Fair Odds */}
        <td className="px-4 py-3 text-right">
          <Tooltip>
            <TooltipTrigger>
              <div>
                {fairOdds ? (
                  <div className="font-mono text-sm font-bold text-foreground">
                    {toAmerican(fairOdds)}
                  </div>
                ) : (
                  <span className="text-muted-foreground text-sm">‚Äî</span>
                )}
              </div>
            </TooltipTrigger>
            <TooltipContent>
              <p>Fair = no-vig price and probability</p>
            </TooltipContent>
          </Tooltip>
        </td>

        {/* Field Odds */}
        <td className="px-4 py-3 w-2/5">
          <div className="flex flex-wrap gap-1">
            {opportunity.fieldPrices?.slice(0, 6).map((price, idx) => (
              <Tooltip key={idx}>
                <TooltipTrigger asChild>
                  <div className="inline-flex items-center h-5 px-1.5 text-xs font-mono bg-muted/20 border border-border/30 rounded cursor-pointer hover:bg-muted/40 transition-colors">
                    <span className="text-muted-foreground text-xs">{price.book}</span>
                    <span className="ml-1 font-medium">{toAmerican(price.odds)}</span>
                  </div>
                </TooltipTrigger>
                <TooltipContent>
                  <p>{price.book}, {toAmerican(price.odds)}, {price.line || 'standard line'}</p>
                </TooltipContent>
              </Tooltip>
            ))}
            {opportunity.fieldPrices?.length > 6 && (
              <Tooltip>
                <TooltipTrigger asChild>
                  <div className="inline-flex items-center h-5 px-1.5 text-xs font-mono bg-muted/20 border border-border/30 rounded cursor-pointer hover:bg-muted/40 transition-colors text-muted-foreground">
                    +{opportunity.fieldPrices.length - 6}
                  </div>
                </TooltipTrigger>
                <TooltipContent>
                  <div className="space-y-1 max-h-48 overflow-y-auto">
                    {opportunity.fieldPrices.slice(6).map((price, idx) => (
                      <p key={idx} className="text-xs">
                        {price.book}: {toAmerican(price.odds)} {price.line && `(${price.line})`}
                      </p>
                    ))}
                  </div>
                </TooltipContent>
              </Tooltip>
            )}
          </div>
        </td>

        {/* Status */}
        <td className="px-4 py-3 text-right">
          <div className="text-xs font-mono text-muted-foreground">
            {toRelTime(opportunity.updatedAt)}
          </div>
        </td>
      </tr>
    </TooltipProvider>
  );
}

import React, { useState, useMemo } from 'react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip';
import { ChevronDown, ChevronUp, Copy, Star, ExternalLink, Clock } from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import { OpportunityRow } from './OpportunityRow';

export interface BettingOpportunity {
  id: string;
  event: {
    home: string;
    away: string;
    league: string;
    startTime: string;
    status: 'live' | 'prematch';
  };
  market: {
    type: 'moneyline' | 'spread' | 'total' | 'player_prop';
    side: string;
    line?: number;
    player?: string;
  };
  fairOdds: number;
  fairProbability: number;
  evPercent: number;
  myPrice: {
    odds: number;
    book: string;
    url?: string;
  };
  fieldPrices: Array<{
    book: string;
    odds: number;
    line?: number;
    url?: string;
  }>;
  consensus?: {
    count: number;
    avgOdds: number;
  };
  updatedAt: string;
  category: 'ev' | 'arbitrage' | 'middling';
  // Expanded details
  kellyStake?: number;
  history?: Array<{ timestamp: string; odds: number }>;
  fullLadder?: Array<{ book: string; odds: number; line?: number }>;
}

interface OpportunityTableProps {
  opportunities: BettingOpportunity[];
  loading?: boolean;
  error?: string;
  onRowClick?: (opportunity: BettingOpportunity) => void;
  className?: string;
}

type SortKey = 'event' | 'market' | 'fairOdds' | 'evPercent' | 'myPrice' | 'updatedAt';
type SortDirection = 'asc' | 'desc';

export function OpportunityTable({ 
  opportunities, 
  loading, 
  error, 
  onRowClick,
  className = ''
}: OpportunityTableProps) {

  const [sortKey, setSortKey] = useState<SortKey>('evPercent');
  const [sortDirection, setSortDirection] = useState<SortDirection>('desc');

  const sortedOpportunities = useMemo(() => {
    if (!opportunities) return [];

    return [...opportunities].sort((a, b) => {
      let aVal: any, bVal: any;

      switch (sortKey) {
        case 'event':
          aVal = `${a.event.home} vs ${a.event.away}`;
          bVal = `${b.event.home} vs ${b.event.away}`;
          break;
        case 'market':
          aVal = `${a.market.type} ${a.market.side}`;
          bVal = `${b.market.type} ${b.market.side}`;
          break;
        case 'fairOdds':
          aVal = a.fairOdds;
          bVal = b.fairOdds;
          break;
        case 'evPercent':
          aVal = a.evPercent;
          bVal = b.evPercent;
          break;
        case 'myPrice':
          aVal = a.myPrice.odds;
          bVal = b.myPrice.odds;
          break;
        case 'updatedAt':
          aVal = new Date(a.updatedAt).getTime();
          bVal = new Date(b.updatedAt).getTime();
          break;
        default:
          return 0;
      }

      if (aVal < bVal) return sortDirection === 'asc' ? -1 : 1;
      if (aVal > bVal) return sortDirection === 'asc' ? 1 : -1;
      return 0;
    });
  }, [opportunities, sortKey, sortDirection]);

  const handleSort = (key: SortKey) => {
    if (sortKey === key) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortKey(key);
      setSortDirection('desc');
    }
  };



  const SortHeader = ({ sortKey: key, children, className: headerClassName = '' }: { 
    sortKey: SortKey; 
    children: React.ReactNode; 
    className?: string;
  }) => (
    <th 
      className={`px-4 py-2 text-left text-xs font-mono text-muted-foreground uppercase tracking-widest cursor-pointer hover:text-foreground transition-colors duration-200 ${headerClassName}`}
      onClick={() => handleSort(key)}
    >
      <div className="flex items-center gap-1">
        {children}
        {sortKey === key && (
          sortDirection === 'asc' ? <ChevronUp className="h-3 w-3" /> : <ChevronDown className="h-3 w-3" />
        )}
      </div>
    </th>
  );

  // Loading state
  if (loading) {
    return (
      <div className={`space-y-2 ${className}`}>
        {Array.from({ length: 5 }).map((_, i) => (
          <div key={i} className="animate-pulse bg-muted/50 h-16 rounded-md" />
        ))}
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className={`text-center py-12 ${className}`}>
        <div className="text-muted-foreground mb-2">Data temporarily unavailable</div>
        <div className="text-sm text-muted-foreground">Please retry shortly</div>
      </div>
    );
  }

  // Empty state
  if (!opportunities || opportunities.length === 0) {
    return (
      <div className={`text-center py-12 ${className}`}>
        <div className="text-muted-foreground">No opportunities match your filters</div>
      </div>
    );
  }

  return (
    <div className={`rounded-lg border border-border/50 overflow-hidden h-full flex flex-col ${className}`}>
      <div className="flex-1 overflow-auto">
        <table className="w-full">
          <thead className="bg-background/95 backdrop-blur-sm border-b border-border/50">
            <tr>
              <th className="px-4 py-2 text-left text-xs font-mono text-muted-foreground uppercase tracking-widest min-w-[80px]">Category</th>
              <SortHeader sortKey="event" className="min-w-[200px]">Event</SortHeader>
              <th className="px-4 py-2 text-left text-xs font-mono text-muted-foreground uppercase tracking-widest min-w-[80px]">League</th>
              <th className="px-4 py-2 text-left text-xs font-mono text-muted-foreground uppercase tracking-widest min-w-[120px]">Prop</th>
              <SortHeader sortKey="market" className="min-w-[100px]">Market</SortHeader>
              <th className="px-4 py-2 text-right text-xs font-mono text-muted-foreground uppercase tracking-widest min-w-[70px] cursor-help"
                  title="Probability of this bet winning after removing vig">
                Hit %
              </th>
              <SortHeader sortKey="evPercent" className="min-w-[80px] text-right">+EV %</SortHeader>
              <SortHeader sortKey="myPrice" className="min-w-[100px]">My Odds</SortHeader>
              <SortHeader sortKey="fairOdds" className="min-w-[90px] text-right">Fair Odds</SortHeader>
              <th className="px-4 py-2 text-left text-xs font-mono text-muted-foreground uppercase tracking-widest w-2/5">
                Field Odds
              </th>
              <SortHeader sortKey="updatedAt" className="min-w-[90px] text-right">Status</SortHeader>
            </tr>
          </thead>
          <tbody className="bg-background">
            {sortedOpportunities.map((opportunity, index) => (
              <OpportunityRow
                key={opportunity.id}
                opportunity={opportunity}
                onClick={() => onRowClick?.(opportunity)}
                isEven={index % 2 === 0}
              />
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

