import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import '@fontsource/press-start-2p';
// import { getSportIcon } from '@/lib/sportsIcons';
import { formatInUserTimezone } from '@/lib/timezone';

interface TickerItem {
  id: string;
  text: string;
  sport: string;
  emoji: string;
}

export function ScoresTicker() {
  const [tickerItems, setTickerItems] = useState<TickerItem[]>([]);

  // Fetch live scores data
  const { data: gamesData } = useQuery({
    queryKey: ['/api/sports/games/today'],
    refetchInterval: 30000, // Refresh every 30 seconds
  }) as { data?: { games?: any[] } };

  const { data: eventsData } = useQuery({
    queryKey: ['/api/sports/events/recent'],
    refetchInterval: 15000, // Refresh every 15 seconds for live events
  }) as { data?: { events?: any[] } };

  useEffect(() => {
    const items: TickerItem[] = [];

    // Process games data
    if (gamesData?.games?.length > 0) {
      gamesData.games.slice(0, 10).forEach((game: any) => {
        const sportEmoji = getSportEmoji(game.sport);
        const timeOrScore = game.team1Score !== undefined && game.team2Score !== undefined
          ? `${game.team1Score} - ${game.team2Score}`
          : formatInUserTimezone(game.time || game.date, 'h:mm a');

        items.push({
          id: `game-${game.gameID}`,
          text: `${sportEmoji} ${game.team1City || ''} ${game.team1Name} vs ${game.team2City || ''} ${game.team2Name} ‚Ä¢ ${timeOrScore}`,
          sport: game.sport,
          emoji: sportEmoji
        });
      });
    }

    // Process live events data
    if (eventsData?.events?.length > 0) {
      eventsData.events.slice(0, 5).forEach((event: any) => {
        const sportEmoji = getSportEmoji(event.sport);
        items.push({
          id: `event-${event.eventID}`,
          text: `${sportEmoji} LIVE: ${event.message} ‚Ä¢ ${event.team1Score || 0} - ${event.team2Score || 0}`,
          sport: event.sport,
          emoji: sportEmoji
        });
      });
    }

    // Always ensure we have some items to display
    if (items.length === 0) {
      // Add a loading message instead of demo data
      items.push({
        id: 'loading',
        text: '‚ö° Loading live sports data...',
        sport: 'loading',
        emoji: '‚ö°'
      });
    }

    setTickerItems(items);
  }, [gamesData, eventsData]);

  const getSportEmoji = (sport: string): string => {
    const emojiMap: { [key: string]: string } = {
      'nfl': 'üèà',
      'nba': 'üèÄ',
      'mlb': '‚öæ',
      'nhl': 'üèí',
      'soccer': '‚öΩ',
      'football': '‚öΩ',
      'tennis': 'üéæ',
      'f1': 'üèéÔ∏è',
      'formula1': 'üèéÔ∏è',
      'mma': 'ü•ä',
      'ufc': 'ü•ä',
      'boxing': 'ü•ä',
      'golf': '‚õ≥',
      'cricket': 'üèè',
      'basketball': 'üèÄ',
      'hockey': 'üèí',
      'baseball': '‚öæ',
      'wnba': 'üèÄ',
    };
    return emojiMap[sport?.toLowerCase()] || 'üèÜ';
  };



  if (tickerItems.length === 0) return null;

  return (
    <>
      {/* Hover trigger zone */}
      <div className="fixed bottom-0 left-0 w-full h-16 z-40 hover-trigger-zone"></div>

      {/* Scores ticker that reveals on hover */}
      <div className="fixed bottom-0 left-0 right-0 z-50 bg-black dark:bg-gray-900 border-t border-green-500/30 dark:border-green-400/30 overflow-hidden h-12 scores-ticker">
        <div className="relative h-full flex items-center">
          <div 
          className="whitespace-nowrap flex items-center animate-scroll"
          style={{
            animation: `scroll ${tickerItems.length * 20}s linear infinite`
          }}
        >
            {/* Duplicate items for seamless loop */}
            {[...tickerItems, ...tickerItems].map((item, index) => (
              <span
                key={`${item.id}-${index}`}
                className="inline-block text-green-400 dark:text-green-300 text-sm font-semibold px-8 tracking-wide"
                style={{ 
                  fontFamily: '"Press Start 2P", "Courier New", monospace',
                  textShadow: '0 0 8px rgba(34, 197, 94, 0.6)',
                  filter: 'brightness(1.1)',
                  fontSize: '11px'
                }}
              >
                <span className="inline-flex items-center gap-1">
                  <span className="mr-1">{item.emoji}</span>
                  {item.text}
                </span>
                <span className="text-green-600 dark:text-green-500 mx-4">|</span>
              </span>
            ))}
          </div>

          {/* Gradient fade edges */}
          <div className="absolute inset-0 pointer-events-none bg-gradient-to-r from-black via-transparent to-black dark:from-gray-900 dark:via-transparent dark:to-gray-900"></div>
        </div>

        <style>{`
          @keyframes scroll {
            0% {
              transform: translateX(100%);
            }
            100% {
              transform: translateX(-100%);
            }
          }

          /* Responsive adjustments */
          @media (max-width: 768px) {
            .px-8 {
              padding-left: 1rem;
              padding-right: 1rem;
            }
          }
        `}</style>
      </div>
    </>
  );
}

